import { Component, Input, Output, EventEmitter } from '@angular/core';

@Component({
  selector: 'app-rating',
  templateUrl: './rating.component.html',
  styleUrls: ['./rating.component.css']
})
export class RatingComponent {
  @Input() name: string;
  @Input() content: string;
  @Input() rate: number;

  constructor() {
    this.name = '';
    this.content = '';
    this.rate = 0;
  }

  ngOnInit() { }
}
