import { Component, Input, Output, EventEmitter } from '@angular/core';

@Component({
  selector: 'app-rating',
  templateUrl: './rating.component.html',
  styleUrls: ['./rating.component.css']
})
export class RatingComponent {
  @Input() name: string;
  @Input() content: string;
  @Input() rate: number;

  constructor() {
    this.name = '';
    this.content = '';
    this.rate = 0;
  }

  checkStar(num : number) {
    if (num == 1) {
      return 'one';
    } else if (num == 2) {
      return 'two';
    } else if (num == 3) {
      return 'three';
    } else if (num == 4) {
      return 'four';
    } else if (num == 5) {
      return 'five';
    }
  }

  ngOnInit() {}
}
